#!/usr/bin/env python3

#Find EcoRI


seq = ''

cut_sites = seq.replace('GAATTC', 'G^AATTC')

fragments =  cut_sites.split('^')

start_pos = 0
for fragment in fragments:
	print(f"This fragment starts at position {start_pos} in the original sequence")
	start_pos += len(fragment)

sorted_fragments = sorted(fragments, key=len, reverse=True)
for fragment in sorted_fragments:
	print(fragment)
	print("spacer")
