#!/usr/bin/env python3

mySet = set('ATGTGGG') # Unique nts
mySet2 = {'ATGCCT'} # single string

SetA = {3, 14, 15, 9, 26, 5, 35, 9}
SetB = {60, 22, 14, 0, 9}

set_intersection = SetA & SetB
print(f'Set intersection is {set_intersection}')

set_difference = SetA - SetB
print(f'The items in SetA and not SetB are {set_difference}')

set_union = SetA | SetB
print(f'The union of sets is {set_union}')

big_seq = ''
big_nt_set = set(big_seq)

nt_dict = {}

for nt in big_nt_set:
	nt_count = big_seq.count(nt)
	nt_dict[nt] = nt_count

print(f'''The nucleotide composition of the large sequence is:
	{nt_dict}''')

